#!/bin/bash

PARAM=$1

if [ $PARAM == logs ]
then
    while [ 1 ]
    do
        echo "~~~~~~" >> /var/ossec/logs/ossec.log
        echo "~~~~~~" >> /var/ossec/logs/ossec.json
        sleep 0.001
    done
elif [ $PARAM == alerts ]
then
    while [ 1 ]
    do
        echo "~~~~~~" >> /var/ossec/logs/alerts/alerts.log
        echo "~~~~~~" >> /var/ossec/logs/alerts/alerts.json
        sleep 0.001
    done
elif [ $PARAM == archives ]
then
     while [ 1 ]
     do
        echo "~~~~~~" >> /var/ossec/logs/archives/archives.log
        echo "~~~~~~" >> /var/ossec/logs/archives/archives.json
        sleep 0.001
     done
else
    echo "Bad log type"
    exit 0
fi
